mapper.select:
select	*
from	STT_USER_INFO
where	STT_ID	=	#{STT_ID}
and		STT_PW	=	#{STT_PW}
and		STT_STATUS	=	10

mapper.update:
update	STT_USER_INFO
set		STT_LEVEL	=	#{STT_LEVEL}
where	STT_SEQ	=	#{STT_SEQ}

update	STT_USER_INFO
set		STT_STATUS	=	#{STT_STATUS}
where	STT_SEQ	=	#{STT_SEQ}

update	STT_USER_INFO
set		STT_PW	=	#{STT_PW}
where	STT_SEQ	=	#{STT_SEQ}
