mapper.insert:
 		INSERT	INTO	
 				STT_LOG_INFO
							(
							STT_DATE
							,STT_CENTER
							,STT_USER
							,STT_IP
							,STT_MENU
							,STT_CONTENTS
							)
				VALUES
							(
							now()
							,#{STT_CENTER}
							,#{STT_USER}
							,#{STT_IP}
							,#{STT_MENU}
							,#{STT_CONTENTS}
							)

mapper.select:
select STT_SEQ ,STT_DATE ,STT_CENTER ,STT_USER ,STT_IP ,STT_MENU ,STT_CONTENTS ,rowNum from ( select STT_SEQ ,STT_DATE ,STT_CENTER ,STT_USER ,STT_IP ,STT_MENU ,STT_CONTENTS ,@row_num:=@row_num + 1 AS rowNum from STT.STT_LOG_INFO ,(SELECT @row_num := 0) rn where STT_SEQ > 0
and date_format(STT_DATE,'%Y-%m-%d') between #{startDate} AND #{endDate}
and STT_USER != #{STT_USER}
and STT_USER = #{STT_USER}
and STT_MENU != #{STT_MENU}
and STT_MENU = #{STT_MENU}
order by
STT_DATE ASC, STT_DATE DESC,
STT_SEQ DESC ) urn where rowNum BETWEEN #{startRow} AND #{endRow} order by rowNum

 		select	count(STT_SEQ) as logSeq
 		from	STT_LOG_INFO
 		where	STT_SEQ > 0
 		
		and		date_format(STT_DATE,'%Y-%m-%d') between #{startDate} AND #{endDate} 
							
		
		and		STT_USER = #{STT_USER}				
		
		
		and		STT_MENU = #{STT_MENU}

select	STT_ID
from	STT_USER_INFO
where	STT_SEQ = #{STT_SEQ}

mapper.update:
update	STT_USER_INFO
set		STT_LEVEL	=	#{STT_LEVEL}
where	STT_SEQ	=	#{STT_SEQ}

update	STT_USER_INFO
set		STT_PW	=	#{STT_PW}
where	STT_SEQ	=	#{STT_SEQ}

mapper.select:
select	COUNT(*)
from	STT_USER_INFO
where	STT_ID = #{STT_ID}

		select	count(R_FILE_NM) as logSeq
 		from	STT_MST_INFO
 		where	R_FILE_NM	!=	''
 		
 			
 					and		STT_DTM like concat(#{endDate},'%')
 				
 					and		R_CUST_NO	=	#{STT_USER_NUM}
 				
 				
 					and		R_EXT_NO	=	#{STT_MEM_NUM}
 				
 					and		STT_MRESULT	like concat('%', #{key_word},'%')
 			
 			
 					and		STT_DTM between #{startDate} AND #{endDate}
				 	
				 	and		R_CUST_NO	=	#{STT_USER_NUM}
				 	
				 	
				 	and		R_EXT_NO	=	#{STT_MEM_NUM}
			 		
			
 			
 					and		STT_DTM between #{startDate} AND #{endDate}

 		SELECT		R_FILE_NM
					,STT_DTM
					,STT_STAT
					,rowNum
		from		(
					SELECT		R_FILE_NM
								,STT_DTM
								,STT_STAT
								,@row_num:=@row_num + 1 AS rowNum
					from		STT.STT_MST_INFO
								,(SELECT @row_num := 0) rn
					WHERE		R_FILE_NM	!=	''
					
			 			
			 					and		STT_DTM like concat(#{endDate},'%')
			 				
			 					and		R_CUST_NO	=	#{STT_USER_NUM}
			 				
			 				
			 					and		R_EXT_NO	=	#{STT_MEM_NUM}
			 				
			 					and		STT_MRESULT	like concat('%', #{key_word},'%')
			 			
			 			
			 					and		STT_DTM between #{startDate} AND #{endDate}
							 	
							 	and		R_CUST_NO	=	#{STT_USER_NUM}
							 	
							 	
							 	and		R_EXT_NO	=	#{STT_MEM_NUM}
						 		
						
			 			
			 					and		STT_DTM between #{startDate} AND #{endDate}
			 			
			 					
					order by 
								STT_DTM DESC
				) urn
		where	rowNum BETWEEN #{startRow} AND #{endRow}
		order by	 rowNum

 		select		STT_MRESULT
		from		STT_MST_INFO
		where		R_FILE_NM = #{STT_CALL}

select	COUNT(*)
from	SWA_MEM_INFO
where	SWA_ID = #{SWA_ID}

select	SWA_NAME
from	SWA_MEM_INFO
where	SWA_SEQ = #{SWA_SEQ}

mapper.update:
update	SWA_MEM_INFO
set		SWA_CENTER	=	#{SWA_CENTER}
		,SWA_INNUM	=	#{SWA_INNUM}
		,SWA_ID		=	#{SWA_ID}
		,SWA_NAME	=	#{SWA_NAME}
where	SWA_SEQ	=	#{SWA_SEQ}

mapper.select:
 		select		r_listen_url
		from		vw_rs_recfile
		where		r_rec_date = #{STT_DTM}
		
		and			r_call_id1 = #{r_call_id1}
		
		
		and			r_call_id2 = #{r_call_id2}
		
		
		and			r_call_id3 = #{r_call_id3}
		
		
		and			r_ext_num = #{r_ext_num}
		
		
		and			r_cust_phone1 = #{r_cust_phone1}
